import "core-js/modules/es.array.push.js";
import "core-js/modules/es.iterator.constructor.js";
import "core-js/modules/es.iterator.filter.js";
import "core-js/modules/es.iterator.for-each.js";
import axios from "axios";
import StaffLeave from "./StaffLeave.vue";
import ChatWindow from "./ChatWindow.vue";
import jsPDF from "jspdf";
import autoTable from "jspdf-autotable";
export default {
  components: {
    StaffLeave,
    ChatWindow
  },
  data() {
    return {
      currentTab: "dashboard",
      showLeaves: false,
      staff: {
        name: "",
        email: "",
        phone: "",
        gender: "",
        address: "",
        pincode: "",
        profilePhoto: "",
        resignationStatus: "none",
        lastWorkingDay: null
      },
      resignationReason: "",
      stats: {
        totalItems: 0,
        leavesTaken: 0,
        pendingLeaves: 0,
        approvedLeaves: 0
      },
      leaveStatus: [],
      selectedFile: null,
      message: "",
      apiBase: "http://localhost:5000",
      defaultPhoto: "/default-avatar.png",
      showProfileModal: false,
      showEditProfile: false,
      unreadMessageCount: 0,
      messageInterval: null
    };
  },
  methods: {
    async fetchProfile() {
      try {
        const token = localStorage.getItem("token");
        const res = await axios.get(`${this.apiBase}/api/staff/me`, {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
        this.staff = res.data.staff;
      } catch {
        this.message = "Error loading profile";
      }
    },
    async fetchStats() {
      try {
        const token = localStorage.getItem("token");
        const user = JSON.parse(localStorage.getItem("user"));
        const email = user?.email;
        const productsRes = await axios.get(`${this.apiBase}/api/products/getproduct`, {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
        let leavesRes = [];
        if (email) {
          try {
            leavesRes = await axios.get(`${this.apiBase}/api/leaves/my`, {
              headers: {
                Authorization: `Bearer ${token}`
              },
              params: {
                email
              }
            });
          } catch (err) {
            console.error("Error fetching leaves:", err);
            leavesRes = [];
          }
        }
        const products = productsRes.data.products || [];
        const leaves = Array.isArray(leavesRes) ? leavesRes : leavesRes.data || [];
        this.stats = {
          totalItems: products.length,
          leavesTaken: leaves.filter(l => l.status === "Approved").length,
          pendingLeaves: leaves.filter(l => l.status === "Pending").length,
          approvedLeaves: leaves.filter(l => l.status === "Approved").length
        };
      } catch (err) {
        console.error("Error fetching stats:", err);
      }
    },
    onFileChange(e) {
      this.selectedFile = e.target.files[0];
    },
    async updateProfile() {
      try {
        const token = localStorage.getItem("token");
        const formData = new FormData();
        Object.keys(this.staff).forEach(key => {
          if (key !== "email") formData.append(key, this.staff[key]);
        });
        if (this.selectedFile) formData.append("profilePhoto", this.selectedFile);
        const res = await axios.put(`${this.apiBase}/api/staff/update`, formData, {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "multipart/form-data"
          }
        });
        this.message = res.data.message;
        this.fetchProfile();
        this.showEditProfile = false;
        setTimeout(() => {
          this.message = "";
        }, 3000);
      } catch (err) {
        this.message = err.response?.data?.message || "Error updating profile";
        setTimeout(() => {
          this.message = "";
        }, 3000);
      }
    },
    toggleLeaves() {
      this.showLeaves = !this.showLeaves;
    },
    logout() {
      localStorage.removeItem("token");
      this.$router.push("/login");
    },
    async fetchLeaveStatus() {
      try {
        const user = JSON.parse(localStorage.getItem("user"));
        const email = user?.email;
        if (!email) return;
        const token = localStorage.getItem("token");
        const res = await axios.get(`${this.apiBase}/api/leaves/my`, {
          headers: {
            Authorization: `Bearer ${token}`
          },
          params: {
            email
          }
        });
        this.leaveStatus = res.data;
      } catch (err) {
        console.error("Error fetching leave status", err);
      }
    },
    formatDate(dateStr) {
      return new Date(dateStr).toLocaleDateString();
    },
    closeProfileModal() {
      this.showProfileModal = false;
      this.showEditProfile = false;
      this.currentTab = "dashboard";
    },
    async fetchUnreadMessages() {
      try {
        const token = localStorage.getItem("token");
        if (!token) return;
        const res = await axios.get("http://localhost:5000/api/messages/unread-count", {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
        if (res.data.success) {
          this.unreadMessageCount = res.data.unreadCount;
        }
      } catch (err) {
        console.error("Error fetching unread messages:", err);
      }
    },
    async submitResignation() {
      try {
        const token = localStorage.getItem("token");
        await axios.post(`${this.apiBase}/api/staff/resign`, {
          reason: this.resignationReason
        }, {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
        this.staff.resignationStatus = "pending";
        this.staff.resignationDate = new Date();
        this.message = "Resignation submitted successfully";
        setTimeout(() => this.message = "", 3000);
      } catch (err) {
        this.message = err.response?.data?.message || "Error submitting resignation";
      }
    },
    downloadExperienceCertificate() {
      const doc = new jsPDF();
      const logo = new Image();
      logo.src = require("@/assets/logo.jpeg");
      doc.addImage(logo, "JPEG", 34, 13, 30, 25);
      doc.setFontSize(16);
      doc.setFont("helvetica", "bold");
      doc.text("COCHIN DISTRIBUTORS", 75, 18);
      doc.setFontSize(11);
      doc.setFont("helvetica", "normal");
      doc.text("Merchant Association Building,", 82, 26);
      doc.text("Santhi Nagar, Kattappana", 87, 32);
      doc.text("Ph: 9447419293, 9446074962", 83, 38);
      doc.setDrawColor(0);
      doc.line(14, 45, 200, 45);
      doc.setFontSize(22);
      doc.setFont("helvetica", "bold");
      doc.text("Experience Certificate", 105, 60, null, null, "center");
      autoTable(doc, {
        startY: 70,
        theme: "grid",
        body: [["Employee Name", this.staff.name], ["Date of Joining", new Date(this.staff.dateOfJoining).toLocaleDateString()], ["Last Working Day", new Date(this.staff.lastWorkingDay).toLocaleDateString()], ["Designation", "Staff"]]
      });
      doc.setFontSize(12);
      doc.setFont("helvetica", "normal");
      const text = `This is to certify that Mr./Ms. ${this.staff.name} has worked with Cochin Distributors from ${new Date(this.staff.dateOfJoining).toLocaleDateString()} to ${new Date(this.staff.lastWorkingDay).toLocaleDateString()}.`;
      const splitText = doc.splitTextToSize(text, 180);
      doc.text(splitText, 14, doc.lastAutoTable.finalY + 15);
      doc.text(`We wish ${this.staff.gender === "female" ? "her" : "him"} all the best for future endeavors.`, 14, doc.lastAutoTable.finalY + 30);
      doc.text("Sincerely,", 14, doc.lastAutoTable.finalY + 50);
      doc.text("Manager", 14, doc.lastAutoTable.finalY + 60);
      doc.text("Cochin Distributors", 14, doc.lastAutoTable.finalY + 65);
      doc.save(`${this.staff.name}_Experience_Certificate.pdf`);
    }
  },
  beforeUnmount() {
    if (this.messageInterval) {
      clearInterval(this.messageInterval);
    }
  },
  watch: {
    currentTab(newTab) {
      if (newTab === "leave-status") this.fetchLeaveStatus();
      if (newTab === "profile") this.showProfileModal = true;
    }
  },
  mounted() {
    this.fetchProfile();
    this.fetchStats();
    this.fetchUnreadMessages();
    this.messageInterval = setInterval(() => {
      this.fetchUnreadMessages();
    }, 3000);
  }
};
