exportStaffPDF(staff) {
  const doc = new jsPDF();

  // 笨 Add logo (left)
  const logo = new Image();
  logo.src = require("@/assets/logo.jpeg");
  doc.addImage(logo, "JPEG", 14, 10, 25, 25);

  // 笨 Company Name + Address (center-left)
  doc.setFontSize(16);
  doc.setFont("helvetica", "bold");
  doc.text("COCHIN DISTRIBUTORS", 45, 18);

  doc.setFontSize(11);
  doc.setFont("helvetica", "normal");
  doc.text("Merchant Association Building,", 45, 26);
  doc.text("Santhi Nagar, Kattappana", 45, 32);
  doc.text("Ph: 9447419293, 9446074962", 45, 38);

  // 笨 Staff Photo (top-right if available)
  if (staff.photo) {
    const staffImg = new Image();
    staffImg.src = staff.photo; // Must be URL or base64
    doc.addImage(staffImg, "JPEG", 160, 10, 35, 35);
  }

  // 笨 Horizontal line under header
  doc.setDrawColor(0);
  doc.line(14, 45, 200, 45);

  // 笨 Personal Info Section
  doc.setFontSize(14);
  doc.setFont("helvetica", "bold");
  doc.text("Personal Information", 14, 55);

  autoTable(doc, {
    startY: 60,
    theme: "grid",
    head: [["Field", "Value"]],
    body: [
      ["Name", staff.name || "窶能

Status", staff.status],
    ],
  });

  // 笨 Footer with timestamp
  let footerY = doc.internal.pageSize.getHeight() - 15;
  doc.setFontSize(10);
  doc.setFont("helvetica", "italic");
  doc.text(
    `Generated on ${new Date().toLocaleDateString()} at ${new Date().toLocaleTimeString()}`,
    14,
    footerY
  );

  doc.save(`${staff.name || "staff"}_details.pdf`);
},

