import "core-js/modules/web.url-search-params.delete.js";
import "core-js/modules/web.url-search-params.has.js";
import "core-js/modules/web.url-search-params.size.js";
import axios from "axios";
export default {
  name: "AddProduct",
  data() {
    return {
      name: "",
      description: "",
      originalPrice: "",
      discountPrice: "",
      category: "",
      // ✅ selected category _id
      categories: [],
      // ✅ all categories
      image: null,
      preview: null,
      loading: false
    };
  },
  methods: {
    handleImageUpload(e) {
      const file = e.target.files[0];
      if (file) {
        this.image = file;
        this.preview = URL.createObjectURL(file);
      }
    },
    async loadCategories() {
      try {
        const res = await axios.get("http://localhost:5000/api/categories");
        this.categories = res.data; // ✅ plain array from backend
      } catch (err) {
        console.error("❌ Error loading categories:", err.message);
        this.categories = [];
      }
    },
    async addProduct() {
      if (!this.image) {
        alert("⚠️ Please select an image");
        return;
      }
      this.loading = true;
      try {
        const formData = new FormData();
        formData.append("name", this.name);
        formData.append("description", this.description);
        formData.append("originalPrice", this.originalPrice);
        formData.append("discountPrice", this.discountPrice);
        formData.append("category", this.category); // ✅ send selected category ID
        formData.append("image", this.image);
        await axios.post("http://localhost:5000/api/products/addproduct", formData, {
          headers: {
            "Content-Type": "multipart/form-data"
          }
        });
        alert("✅ Product added successfully!");
        this.resetForm();
      } catch (err) {
        alert("❌ Error adding product");
        console.error("AddProduct error:", err.response?.data || err.message);
      } finally {
        this.loading = false;
      }
    },
    resetForm() {
      this.name = "";
      this.description = "";
      this.originalPrice = "";
      this.discountPrice = "";
      this.category = "";
      this.image = null;
      this.preview = null;
    }
  },
  mounted() {
    this.loadCategories(); // ✅ load categories on mount
  }
};
