import axios from "axios";
import jsPDF from "jspdf";
import autoTable from "jspdf-autotable";
import Chart from "chart.js/auto";
export default {
  data() {
    return {
      email: "",
      staff: [],
      searchQuery: "",
      statusFilter: "",
      toastMessage: "",
      toastError: false,
      showDeleteModal: false,
      showInviteForm: false,
      deleteId: null,
      activeMenu: null,
      statusChartInstance: null,
      departmentChartInstance: null
    };
  },
  computed: {
    filteredStaff() {
      return this.staff.filter(s => {
        const matchesSearch = !this.searchQuery || s.name?.toLowerCase().includes(this.searchQuery.toLowerCase()) || s.email?.toLowerCase().includes(this.searchQuery.toLowerCase());
        const matchesStatus = !this.statusFilter || s.status === this.statusFilter;
        return matchesSearch && matchesStatus;
      });
    },
    stats() {
      return {
        total: this.staff.length,
        pending: this.staff.filter(s => s.status === "pending").length,
        active: this.staff.filter(s => s.status === "active").length,
        deactivated: this.staff.filter(s => s.status === "deactivated").length
      };
    },
    departmentData() {
      const departments = {};
      this.staff.forEach(s => {
        const dept = s.department || "Unassigned";
        departments[dept] = (departments[dept] || 0) + 1;
      });
      return departments;
    }
  },
  methods: {
    async fetchStaff() {
      try {
        const res = await axios.get("http://localhost:5000/api/staff");
        this.staff = res.data.staff;
      } catch (err) {
        this.showToast("Failed to fetch staff", true);
      }
    },
    async inviteStaff() {
      try {
        await axios.post("http://localhost:5000/api/staff/invite", {
          email: this.email
        });
        this.showToast("Invitation sent!");
        this.email = "";
        this.fetchStaff();
      } catch (err) {
        this.showToast(err.response?.data?.message || "Error sending invite", true);
      }
    },
    async approveStaff(id) {
      try {
        const joiningDate = new Date().toISOString();
        const res = await axios.put(`http://localhost:5000/api/staff/approve/${id}`, {
          joiningDate
        });
        this.showToast(res.data.message || "Staff approved successfully");
        this.fetchStaff();
      } catch (err) {
        this.showToast(err.response?.data?.message || "Error approving staff", true);
      }
    },
    async setStatus(id, status) {
      try {
        await axios.put(`http://localhost:5000/api/staff/status/${id}`, {
          status
        });
        this.showToast("Status updated successfully");
        this.fetchStaff();
      } catch (err) {
        this.showToast("Failed to update status", true);
      }
    },
    // --- Delete with Modal ---
    openDeleteModal(id) {
      this.deleteId = id;
      this.showDeleteModal = true;
    },
    cancelDelete() {
      this.showDeleteModal = false;
      this.deleteId = null;
    },
    async confirmDelete() {
      try {
        await axios.delete(`http://localhost:5000/api/staff/${this.deleteId}`);
        this.showToast("Staff deleted successfully");
        this.fetchStaff();
      } catch (err) {
        this.showToast(err.response?.data?.message || "Error deleting staff", true);
      }
      this.showDeleteModal = false;
      this.deleteId = null;
    },
    toggleMenu(staffId) {
      this.activeMenu = this.activeMenu === staffId ? null : staffId;
    },
    showToast(message, isError = false) {
      this.toastMessage = message;
      this.toastError = isError;
      setTimeout(() => {
        this.toastMessage = "";
        this.toastError = false;
      }, 3000);
    },
    // --- PDF Methods (unchanged) ---
    exportStaffPDF(staff) {
      const doc = new jsPDF();
      const logo = new Image();
      logo.src = require("@/assets/logo.jpeg");
      doc.addImage(logo, "JPEG", 34, 13, 30, 25);
      doc.setFontSize(16);
      doc.setFont("helvetica", "bold");
      doc.text("COCHIN DISTRIBUTORS", 75, 18);
      doc.setFontSize(11);
      doc.setFont("helvetica", "normal");
      doc.text("Merchant Association Building,", 82, 26);
      doc.text("Santhi Nagar, Kattappana", 87, 32);
      doc.text("Ph: 9447419293, 9446074962", 83, 38);
      doc.setDrawColor(0);
      doc.line(14, 45, 200, 45);
      if (staff.profilePhoto) {
        const staffImg = new Image();
        staffImg.src = `http://localhost:5000${staff.profilePhoto}`;
        doc.addImage(staffImg, "JPEG", 160, 10, 35, 35);
      }
      doc.setFontSize(14);
      doc.setFont("helvetica", "bold");
      doc.text("Personal Information", 14, 55);
      autoTable(doc, {
        startY: 60,
        theme: "grid",
        head: [["Field", "Value"]],
        body: [["Name", staff.name || "‚Äî"], ["Email", staff.email], ["Phone", staff.phone || "‚Äî"], ["Gender", staff.gender || "‚Äî"], ["Address", staff.address || "‚Äî"], ["Pincode", staff.pincode || "‚Äî"]]
      });
      let finalY = doc.lastAutoTable.finalY + 10;
      doc.setFontSize(14);
      doc.setFont("helvetica", "bold");
      doc.text("Work Information", 14, finalY);
      autoTable(doc, {
        startY: finalY + 5,
        theme: "grid",
        head: [["Field", "Value"]],
        body: [["Date of Joining", staff.dateOfJoining ? new Date(staff.dateOfJoining).toLocaleDateString() : "‚Äî"], ["Status", staff.status || "‚Äî"]]
      });
      let footerY = doc.internal.pageSize.getHeight() - 15;
      doc.setFontSize(10);
      doc.setFont("helvetica", "italic");
      doc.text(`Generated on ${new Date().toLocaleDateString()} at ${new Date().toLocaleTimeString()}`, 14, footerY);
      doc.save(`${staff.name || "staff"}_details.pdf`);
    },
    exportAllStaffPDF() {
      const doc = new jsPDF();
      const logo = new Image();
      logo.src = require("@/assets/logo.jpeg");
      doc.addImage(logo, "JPEG", 34, 13, 30, 25);
      doc.setFontSize(16);
      doc.setFont("helvetica", "bold");
      doc.text("COCHIN DISTRIBUTORS", 75, 18);
      doc.setFontSize(11);
      doc.setFont("helvetica", "normal");
      doc.text("Merchant Association Building,", 82, 26);
      doc.text("Santhi Nagar, Kattappana", 87, 32);
      doc.text("Ph: 9447419293, 9446074962", 83, 38);
      doc.setDrawColor(0);
      doc.line(14, 45, 200, 45);
      autoTable(doc, {
        startY: 50,
        head: [["Name", "Email", "Phone", "Gender", "Address", "Pincode", "Date of Joining", "Status"]],
        body: this.staff.map(s => [s.name || "‚Äî", s.email, s.phone || "‚Äî", s.gender || "‚Äî", s.address || "‚Äî", s.pincode || "‚Äî", s.dateOfJoining ? new Date(s.dateOfJoining).toLocaleDateString() : "‚Äî", s.status || "‚Äî"])
      });
      let footerY = doc.internal.pageSize.getHeight() - 15;
      doc.setFontSize(10);
      doc.setFont("helvetica", "italic");
      doc.text(`Generated on ${new Date().toLocaleDateString()} at ${new Date().toLocaleTimeString()}`, 14, footerY);
      doc.save("staff-report.pdf");
    },
    initStatusChart() {
      if (this.statusChartInstance) {
        this.statusChartInstance.destroy();
      }
      const ctx = this.$refs.statusChart;
      if (!ctx) return;
      this.statusChartInstance = new Chart(ctx, {
        type: "line",
        data: {
          labels: ["Pending", "Active", "Deactivated"],
          datasets: [{
            label: "Employee Count",
            data: [this.stats.pending, this.stats.active, this.stats.deactivated],
            borderColor: "#3b82f6",
            backgroundColor: "rgba(59, 130, 246, 0.1)",
            fill: true,
            tension: 0.4,
            borderWidth: 3,
            pointRadius: 6,
            pointBackgroundColor: "#3b82f6",
            pointBorderColor: "#fff",
            pointBorderWidth: 2
          }]
        },
        options: {
          responsive: true,
          maintainAspectRatio: true,
          plugins: {
            legend: {
              display: true,
              position: "top"
            }
          },
          scales: {
            y: {
              beginAtZero: true,
              ticks: {
                stepSize: 1
              }
            }
          }
        }
      });
    },
    initDepartmentChart() {
      if (this.departmentChartInstance) {
        this.departmentChartInstance.destroy();
      }
      const ctx = this.$refs.departmentChart;
      if (!ctx) return;
      const deptNames = Object.keys(this.departmentData);
      const deptCounts = Object.values(this.departmentData);
      this.departmentChartInstance = new Chart(ctx, {
        type: "doughnut",
        data: {
          labels: deptNames,
          datasets: [{
            data: deptCounts,
            backgroundColor: ["#3b82f6", "#10b981", "#f59e0b", "#ef4444", "#8b5cf6", "#06b6d4"],
            borderColor: "#fff",
            borderWidth: 2
          }]
        },
        options: {
          responsive: true,
          maintainAspectRatio: true,
          plugins: {
            legend: {
              display: true,
              position: "bottom"
            }
          }
        }
      });
    }
  },
  mounted() {
    this.fetchStaff();
  },
  watch: {
    staff() {
      this.$nextTick(() => {
        this.initStatusChart();
        this.initDepartmentChart();
      });
    }
  }
};
