  data() {
    return {
      // We don't want Vue to make these reactive because Three.js objects are complex
      // and Vue's proxying can interfere with internal properties like modelViewMatrix
    };
  },
  mounted() {
    // Store Three.js objects as non-reactive properties on the instance
    this.scene = null;
    this.camera = null;
    this.renderer = null;
    this.cube = null;
    this.model = null;
    this.controls = null;
    this.animationId = null;
    this.initThree();
    window.addEventListener("resize", this.handleResize);
  },
  beforeUnmount() {
    this.cleanup();
    window.removeEventListener("resize", this.handleResize);
  },
  methods: {
    initThree() {
      const container = this.$refs.container;
      const width = container.clientWidth;
      const height = container.clientHeight;

      // Scene
      this.scene = new THREE.Scene();
      this.scene.background = new THREE.Color(0xf0f0f0);

      // Camera
      this.camera = new THREE.PerspectiveCamera(75, width / height, 0.1, 1000);
      this.camera.position.z = 2;

      // Renderer
      this.renderer = new THREE.WebGLRenderer({
        antialias: true
      });
      this.renderer.setSize(width, height);
      container.appendChild(this.renderer.domElement);

      // Controls
      this.controls = new OrbitControls(this.camera, this.renderer.domElement);
      this.controls.enableDamping = true;
      this.controls.dampingFactor = 0.05;

      // Lights
      const ambientLight = new THREE.AmbientLight(0xffffff, 0.8);
      this.scene.add(ambientLight);
      const directionalLight = new THREE.DirectionalLight(0xffffff, 1);
      directionalLight.position.set(5, 5, 5);
      this.scene.add(directionalLight);
      const backLight = new THREE.DirectionalLight(0xffffff, 0.5);
      backLight.position.set(-5, -5, -5);
      this.scene.add(backLight);
      const textureLoader = new THREE.TextureLoader();
      if (this.model3D) {
        this.loadModel();
      } else {
        // Check if we have front+back images (new format) or single image (legacy format)
        const has2Images = this.imageFront && this.imageBack;
        if (has2Images) {
          this.loadAndCreateCubeWith3Images(textureLoader);
        } else if (this.image) {
          this.loadAndCreateCubeWithSingleImage(textureLoader);
        } else {
          console.error("No image or model provided");
        }
      }
      this.animate();
    },
    loadModel() {
      const loader = new GLTFLoader();
      loader.load(this.model3D, gltf => {
        this.model = gltf.scene;

        // Center the model
        const box = new THREE.Box3().setFromObject(this.model);
        const center = box.getCenter(new THREE.Vector3());
        const size = box.getSize(new THREE.Vector3());
        const maxDim = Math.max(size.x, size.y, size.z);
        const scale = 1.5 / maxDim;
        this.model.scale.setScalar(scale);
        this.model.position.sub(center.multiplyScalar(scale));
        this.scene.add(this.model);
      }, () => {
        // console.log((xhr.loaded / xhr.total * 100) + '% loaded');
      }, error => {
        console.error("An error happened loading the model:", error);
      });
    },
    loadAndCreateCubeWithSingleImage(textureLoader) {
      textureLoader.load(this.image, texture => {
        const geometry = new THREE.BoxGeometry(1, 1, 1);
        const material = new THREE.MeshStandardMaterial({
          map: texture
        });
        this.cube = new THREE.Mesh(geometry, material);
        this.scene.add(this.cube);
      }, undefined, err => {
        console.error("Error loading texture:", err);
      });
    },
    loadAndCreateCubeWith3Images(textureLoader) {
      // Load front and back images (required), side, top, and bottom optional
      const texturePromises = [this.loadTexture(textureLoader, this.imageFront), this.loadTexture(textureLoader, this.imageBack), this.imageSide ? this.loadTexture(textureLoader, this.imageSide) : Promise.resolve(null), this.imageTop ? this.loadTexture(textureLoader, this.imageTop) : Promise.resolve(null), this.imageBottom ? this.loadTexture(textureLoader, this.imageBottom) : Promise.resolve(null)];
      Promise.all(texturePromises).then(([frontTexture, backTexture, sideTexture, topTexture, bottomTexture]) => {
        // Realistic packet dimensions: width x height x depth
        // Typically product packets are taller than wide
        const geometry = new THREE.BoxGeometry(0.6, 1.2, 0.3);

        // Gray material for edges (top, bottom, left, right if no side image)
        const grayMaterial = new THREE.MeshStandardMaterial({
          color: 0xd0d0d0,
          roughness: 0.7
        });

        // Create material array for 6 faces:
        // [right(0), left(1), top(2), bottom(3), front(4), back(5)]
        const materials = [];

        // Left and right faces (use side image if available, else gray)
        if (sideTexture) {
          materials.push(new THREE.MeshStandardMaterial({
            map: sideTexture
          })); // right (0)
          materials.push(new THREE.MeshStandardMaterial({
            map: sideTexture
          })); // left (1)
        } else {
          materials.push(grayMaterial.clone()); // right (0)
          materials.push(grayMaterial.clone()); // left (1)
        }

        // Top and bottom faces (use separate images if available, else gray)
        if (topTexture) {
          materials.push(new THREE.MeshStandardMaterial({
            map: topTexture
          })); // top (2)
        } else {
          materials.push(grayMaterial.clone()); // top (2)
        }
        if (bottomTexture) {
          materials.push(new THREE.MeshStandardMaterial({
            map: bottomTexture
          })); // bottom (3)
        } else {
          materials.push(grayMaterial.clone()); // bottom (3)
        }

        // Front and back faces (required)
        materials.push(new THREE.MeshStandardMaterial({
          map: frontTexture
        })); // front (4)
        materials.push(new THREE.MeshStandardMaterial({
          map: backTexture
        })); // back (5)

        this.cube = new THREE.Mesh(geometry, materials);
        this.scene.add(this.cube);
      }).catch(err => {
        console.error("Error loading textures:", err);
      });
    },
    loadTexture(textureLoader, imagePath) {
      return new Promise((resolve, reject) => {
        textureLoader.load(imagePath, texture => resolve(texture), undefined, err => reject(err));
      });
    },
    animate() {
      this.animationId = requestAnimationFrame(this.animate);
      if (this.controls) {
        this.controls.update();
      }

      // Only auto-rotate if it's the cube (fallback mode)
      if (this.cube) {
        this.cube.rotation.x += 0.005;
        this.cube.rotation.y += 0.01;
      }
      if (this.renderer && this.scene && this.camera) {
        this.renderer.render(this.scene, this.camera);
      }
    },
    handleResize() {
      if (!this.$refs.container) return;
      const width = this.$refs.container.clientWidth;
      const height = this.$refs.container.clientHeight;
      if (this.camera) {
        this.camera.aspect = width / height;
        this.camera.updateProjectionMatrix();
      }
      if (this.renderer) {
        this.renderer.setSize(width, height);
      }
